buyStrategies.Buy_Based_on_Price_Point:
function(barSeries, index, stockWatch){
            closePriceIndicator = new ClosePriceIndicator(barSeries);
            if (closePriceIndicator.getValue(index).floatValue() < 500) {
                return true;
            }
            return false;
        };

buyStrategies.Buy_Based_on_30_SMA:
function(barSeries, index, stockWatch){
        closePriceIndicator = new ClosePriceIndicator(barSeries);
        sma30Indicator = new SMAIndicator(closePriceIndicator, 30);

        if (closePriceIndicator.getValue(index).floatValue() >
            sma30Indicator.getValue(index).floatValue()) {
            return true;
        }
        return false;
    };

buyStrategies.Buy_Based_on_5EMA_AND_30SMA:
function(barSeries, index, stockWatch){
    sma30Indicator = new SMAIndicator(closePriceIndicator, 30);
    ema5Indicator = new EMAIndicator(closePriceIndicator, 5);

    if (ema5Indicator.getValue(index).floatValue() >
        sma30Indicator.getValue(index).floatValue()) {
        return true;
    }
    return false;
};

